const request = $('Critic Request').first().json.body;
const aiResponse = $input.first().json;
let evaluation = { scores: { clarity: 5, persuasiveness: 5, specificity: 5, emotional_appeal: 5, funder_alignment: 5, overall: 5 }, issues: [], strengths: [], weaknesses: [] };
try {
  const responseText = aiResponse.message?.content || aiResponse.text || '';
  const jsonMatch = responseText.match(/\{[\s\S]*\}/);
  if (jsonMatch) evaluation = JSON.parse(jsonMatch[0]);
} catch (e) { evaluation.parseError = e.message; }
if (!evaluation.scores.overall) {
  const s = evaluation.scores;
  evaluation.scores.overall = Math.round(((s.clarity + s.persuasiveness + s.specificity + s.emotional_appeal + s.funder_alignment) / 5) * 10) / 10;
}
const issuesByType = {};
for (const issue of evaluation.issues || []) { const t = issue.type || 'OTHER'; if (!issuesByType[t]) issuesByType[t] = []; issuesByType[t].push(issue); }
const priorityIssues = (evaluation.issues || []).filter(i => ['PASSIVE_VOICE','JARGON','VAGUE_CLAIMS'].includes(i.type)).slice(0, 5);
return [{ json: { funderName: request.funderName, grantPurpose: request.grantPurpose, proposalText: request.proposalText, scores: evaluation.scores, issuesByType, issueCount: (evaluation.issues || []).length, strengths: evaluation.strengths || [], weaknesses: evaluation.weaknesses || [], priorityIssues, needsRewrite: evaluation.scores.overall < 7 } }];

