const evalData = $('Parse Evaluation').first().json;
const aiResponse = $input.first().json;
let rewrites = { rewrites: [], improved_opening: null, improved_closing: null, story_suggestion: null, quick_wins: [] };
try {
  const responseText = aiResponse.message?.content || aiResponse.text || '';
  const jsonMatch = responseText.match(/\{[\s\S]*\}/);
  if (jsonMatch) rewrites = JSON.parse(jsonMatch[0]);
} catch (e) { rewrites.parseError = e.message; }
const scoreEmoji = (score) => { if (score >= 8) return '‚úÖ'; if (score >= 6) return '‚ö†Ô∏è'; return '‚ùå'; };
let report = `# Proposal Review Report

**Funder:** ${evalData.funderName}
**Purpose:** ${evalData.grantPurpose}
**Review Date:** ${new Date().toISOString().split('T')[0]}

---

## Overall Score: ${evalData.scores.overall}/10

### Score Breakdown

| Dimension | Score | Status |
|-----------|-------|--------|
| Clarity | ${evalData.scores.clarity}/10 | ${scoreEmoji(evalData.scores.clarity)} |
| Persuasiveness | ${evalData.scores.persuasiveness}/10 | ${scoreEmoji(evalData.scores.persuasiveness)} |
| Specificity | ${evalData.scores.specificity}/10 | ${scoreEmoji(evalData.scores.specificity)} |
| Emotional Appeal | ${evalData.scores.emotional_appeal}/10 | ${scoreEmoji(evalData.scores.emotional_appeal)} |
| Funder Alignment | ${evalData.scores.funder_alignment}/10 | ${scoreEmoji(evalData.scores.funder_alignment)} |

`;
if (evalData.strengths.length > 0) { report += `### ‚úÖ Strengths
`; for (const s of evalData.strengths) report += `- ${s}
`; report += `
`; }
if (evalData.weaknesses.length > 0) { report += `### ‚ùå Areas for Improvement
`; for (const w of evalData.weaknesses) report += `- ${w}
`; report += `
`; }
report += `### üîç Issues Found (${evalData.issueCount} total)

`;
for (const [type, issues] of Object.entries(evalData.issuesByType)) { report += `**${type}** (${issues.length})
`; for (const issue of issues.slice(0, 3)) report += `- "${issue.text}" ‚Üí *${issue.suggestion}*
`; report += `
`; }
if (rewrites.rewrites && rewrites.rewrites.length > 0) { report += `---

## üìù Suggested Rewrites

`; for (const r of rewrites.rewrites) { report += `**Before:** "${r.original}"
**After:** "${r.improved}"
*Why:* ${r.reason}

`; } }
if (rewrites.improved_opening) report += `### Improved Opening Paragraph
${rewrites.improved_opening}

`;
if (rewrites.improved_closing) report += `### Improved Closing Paragraph
${rewrites.improved_closing}

`;
if (rewrites.story_suggestion) report += `### üí° Story to Add
${rewrites.story_suggestion}

`;
if (rewrites.quick_wins && rewrites.quick_wins.length > 0) { report += `### ‚ö° Quick Wins
`; for (const qw of rewrites.quick_wins) report += `- [ ] ${qw}
`; report += `
`; }
report += `---
*Generated by AI Proposal Critic*`;
let recommendation = 'READY TO SUBMIT';
if (evalData.scores.overall < 5) recommendation = 'MAJOR REVISION NEEDED';
else if (evalData.scores.overall < 7) recommendation = 'REVISION RECOMMENDED';
else if (evalData.scores.overall < 8) recommendation = 'MINOR TWEAKS SUGGESTED';
return [{ json: { success: true, recommendation, overallScore: evalData.scores.overall, scores: evalData.scores, issueCount: evalData.issueCount, strengthCount: evalData.strengths.length, weaknessCount: evalData.weaknesses.length, rewriteCount: (rewrites.rewrites || []).length, quickWins: rewrites.quick_wins || [], strengths: evalData.strengths, weaknesses: evalData.weaknesses, issuesByType: evalData.issuesByType, rewrites: rewrites.rewrites || [], improvedOpening: rewrites.improved_opening, improvedClosing: rewrites.improved_closing, storySuggestion: rewrites.story_suggestion, reportMarkdown: report } }];

const critic = $json;
  const proposal = $('Critic Request').first().json.body;

  const overallScore = critic.overallScore || 0;
  const alignmentScore = proposal.alignmentScore || 0;
  const complianceScore = proposal.complianceScore || 0;

  // Budget data
  const budget = proposal.budget || {};
  const budgetAmount = proposal.budgetAmount || budget.amount || 0;
  const hasBudget = budget.generated === true;

  // Determine color
  let color = '#e01e5a';
  if (overallScore >= 8) color = '#36a64f';
  else if (overallScore >= 6) color = '#f2c744';

  // Build lists
  const strengthsList = (critic.strengths || []).slice(0, 3).map(s => '‚Ä¢ ' + s).join('\n') || 'None listed';
  const weaknessesList = (critic.weaknesses || []).slice(0, 3).map(w => '‚Ä¢ ' + w).join('\n') || 'None listed';
  const quickWinsList = (critic.quickWins || []).slice(0, 3).map(q => '‚Ä¢ ' + q).join('\n') || 'None listed';

  // Ready status
  const isReady = overallScore >= 7 && complianceScore >= 90;
  const readyText = isReady ? '‚úÖ *Ready to submit!*' : 'üìù *Review recommended before submitting*';

  // Build blocks array - use explicit any type to avoid TS issues
  const blocks = [];

  blocks.push({
    type: "header",
    text: {
      type: "plain_text",
      text: "üìù Proposal Generated & Reviewed",
      emoji: true
    }
  });

  blocks.push({
    type: "section",
    fields: [
      { type: "mrkdwn", text: "*Funder:*\n" + (proposal.funderName || 'Unknown') },
      { type: "mrkdwn", text: "*Deadline:*\n" + (proposal.deadline || 'Not specified') }
    ]
  });

  blocks.push({
    type: "section",
    fields: [
      { type: "mrkdwn", text: "*Alignment:*\n" + (alignmentScore >= 80 ? 'üü¢' : 'üü°') + " " + alignmentScore + "%" },
      { type: "mrkdwn", text: "*Compliance:*\n" + (complianceScore >= 90 ? 'üü¢' : 'üü°') + " " + complianceScore + "%" }
    ]
  });

  // ADD BUDGET SECTION
  blocks.push({ type: "divider" });

  if (hasBudget) {
    const personnelAmt = (budget.categoryTotals && budget.categoryTotals.personnel) ?
  budget.categoryTotals.personnel.amount : 0;
    const personnelPct = (budget.categoryTotals && budget.categoryTotals.personnel) ?
  budget.categoryTotals.personnel.percent : 0;
    const directAmt = (budget.categoryTotals && budget.categoryTotals.direct) ? budget.categoryTotals.direct.amount : 0;
    const directPct = (budget.categoryTotals && budget.categoryTotals.direct) ? budget.categoryTotals.direct.percent :
  0;
    const indirectAmt = (budget.categoryTotals && budget.categoryTotals.indirect) ?
  budget.categoryTotals.indirect.amount : 0;
    const adminPct = budget.adminPercent || 0;

    const budgetText = "*üí∞ Budget Summary*\n" +
      "‚Ä¢ *Amount:* $" + budgetAmount.toLocaleString() + "\n" +
      "‚Ä¢ *Personnel:* $" + personnelAmt.toLocaleString() + " (" + personnelPct + "%)\n" +
      "‚Ä¢ *Direct Program:* $" + directAmt.toLocaleString() + " (" + directPct + "%)\n" +
      "‚Ä¢ *Admin:* $" + indirectAmt.toLocaleString() + " (" + adminPct + "%)";

    blocks.push({
      type: "section",
      text: { type: "mrkdwn", text: budgetText }
    });
  } else {
    blocks.push({
      type: "section",
      text: { type: "mrkdwn", text: "*üí∞ Budget:* ‚ö†Ô∏è Not generated" }
    });
  }

  blocks.push({ type: "divider" });

  blocks.push({
    type: "section",
    text: { type: "mrkdwn", text: "*üîç Critic Score:* " + overallScore + "/10 ‚Äî " + (critic.recommendation || 'N/A') }
  });

  blocks.push({
    type: "section",
    fields: [
      { type: "mrkdwn", text: "*Clarity:* " + (critic.scores && critic.scores.clarity ? critic.scores.clarity : 0) +
  "/10" },
      { type: "mrkdwn", text: "*Persuasiveness:* " + (critic.scores && critic.scores.persuasiveness ?
  critic.scores.persuasiveness : 0) + "/10" },
      { type: "mrkdwn", text: "*Specificity:* " + (critic.scores && critic.scores.specificity ?
  critic.scores.specificity : 0) + "/10" },
      { type: "mrkdwn", text: "*Funder Fit:* " + (critic.scores && critic.scores.funder_alignment ?
  critic.scores.funder_alignment : 0) + "/10" }
    ]
  });

  blocks.push({ type: "divider" });

  blocks.push({
    type: "section",
    text: { type: "mrkdwn", text: "*‚úÖ Strengths (" + (critic.strengthCount || 0) + "):*\n" + strengthsList }
  });

  blocks.push({
    type: "section",
    text: { type: "mrkdwn", text: "*‚ö†Ô∏è To Improve (" + (critic.weaknessCount || 0) + "):*\n" + weaknessesList }
  });

  blocks.push({ type: "divider" });

  blocks.push({
    type: "section",
    text: { type: "mrkdwn", text: "*‚ö° Quick Wins:*\n" + quickWinsList }
  });

  // Only add story suggestion if it exists
  if (critic.storySuggestion && critic.storySuggestion.trim()) {
    blocks.push({
      type: "section",
      text: { type: "mrkdwn", text: "*üí° Story to Add:*\n" + critic.storySuggestion.substring(0, 200) + "..." }
    });
  }

  // Add final blocks
  blocks.push({ type: "divider" });

  blocks.push({
    type: "section",
    text: { type: "mrkdwn", text: readyText }
  });

  const contextText = (critic.rewriteCount || 0) + " rewrites | " +
    (critic.issueCount || 0) + " issues | " +
    (proposal.warningsCount || 0) + " compliance warnings | Budget: " +
    (hasBudget ? "$" + budgetAmount.toLocaleString() : "Not generated");

  blocks.push({
    type: "context",
    elements: [
      { type: "mrkdwn", text: contextText }
    ]
  });

  const slackMessage = {
    text: "Proposal Ready: " + (proposal.grantPurpose || 'Grant Proposal'),
    attachments: [
      {
        color: color,
        blocks: blocks
      }
    ]
  };
 const googleDocUrl = proposal.googleDocUrl || '';
  if (googleDocUrl) {
    blocks.push({
      type: "section",
      text: { type: "mrkdwn", text: "*üìÑ Full Proposal:*" },
      accessory: {
        type: "button",
        text: {
          type: "plain_text",
          text: "Open Google Doc",
          emoji: true
        },
        url: googleDocUrl,
        action_id: "open_google_doc"
      }
    });
  }

  return [{ json: slackMessage }];
